import React, {useState} from 'react';
import Layout from '../components/Layout';
import {useMutation, gql} from '@apollo/client';
import {useRouter} from "next/router";

import {useFormik} from "formik";
import * as Yup from "yup";

const CREAR_CUENTA = gql`
    mutation NuevoUsuario($input: inputUsuario) {
      nuevoUsuario(input: $input) {
        id
        nombre
        apellido
        email
        creado
      }
    }
`
//Manjar el estado para los mensajes
const [mensaje,setMensaje] = useState(null);

//Mutation para crear nuevos usuarios o vendedores (CREAR NUEVA CUENTA)
const [nuevoUsuario] = useMutation(CREAR_CUENTA);

const router = useRouter()

const NuevaCuenta = () => {

    //Aqui validamos el formulario de Nueva Cuenta (Usuario o Vendedor)
    const formik = useFormik({
        initialValues: {
            nombre: '',
            apellido: '',
            email: '',
            password: ''
        },
        validationSchema: Yup.object({
            nombre: Yup.string().required('El nombre es requerido'),
            apellido: Yup.string().required('El apellido es requerido'),
            email: Yup.string().email('El email no es valido').required('El email es requerido'),
            password: Yup.string().required('El password es requerido').min(6, 'El Password debe tener al menos seis caracteres')
        }),
        onSubmit: async (datos) => {
            const {nombre, apellido, email, password} = datos;

            try {
                const {data} = await nuevoUsuario({
                    variables: {
                        nombre,
                        apellido,
                        email,
                        password
                    }
                })
            }catch(err) {
                console.log(err);
            }
        }
    })


    return (
        <>
            <Layout>
                <h1 className="text-center text-2xl text-white font-light">Crear Nueva Cuenta</h1>
                <div className="flex justify-center mt-5">
                    <div className="w-full max-w-sm">
                        <form className="bg-white rounded shadow-md px-8 pt-6 pb-8 mb-4" onSubmit={formik.handleSubmit}>
                            <div className="mb-4">
                                <label className="block text-gray-700 font-bold mb-2" htmlFor="email">
                                    Nombre
                                </label>
                                <input
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    id="nombre"
                                    type="text"
                                    placeholder="Nombre Usuario"
                                    value={formik.values.nombre}
                                    onChange={formik.handleChange}
                                    onBlur={formik.handleBlur}
                                />
                                {
                                    formik.touched.nombre && formik.errors.nombre ? (
                                        <div className="my-2 bg-red-100 border-red-500 text-red-700 p-2">
                                            <p>Error</p>
                                            <p>{formik.errors.nombre}</p>
                                        </div>
                                    ) : null
                                }
                            </div>
                            <div className="mb-4">
                                <label className="block text-gray-700 font-bold mb-2" htmlFor="email">
                                    Apellido
                                </label>
                                <input
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    id="apellido"
                                    type="text"
                                    placeholder="Apellido Usuario"
                                    value={formik.values.apellido}
                                    onChange={formik.handleChange}
                                    onBlur={formik.handleBlur}
                                />
                                {
                                    formik.touched.apellido && formik.errors.apellido ? (
                                        <div className="my-2 bg-red-100 border-red-500 text-red-700 p-2">
                                            <p>Error</p>
                                            <p>{formik.errors.apellido}</p>
                                        </div>
                                    ) : null
                                }
                            </div>
                            <div className="mb-4">
                                <label className="block text-gray-700 font-bold mb-2" htmlFor="email">
                                    Email
                                </label>
                                <input
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    id="email"
                                    type="email"
                                    placeholder="Email Usuario"
                                    value={formik.values.email}
                                    onChange={formik.handleChange}
                                    onBlur={formik.handleBlur}
                                />
                                {
                                    formik.touched.email && formik.errors.email ? (
                                        <div className="my-2 bg-red-100 border-red-500 text-red-700 p-2">
                                            <p>Error</p>
                                            <p>{formik.errors.email}</p>
                                        </div>
                                    ) : null
                                }
                            </div>
                            <div className="mb-4">
                                <label className="block text-gray-700 font-bold mb-2" htmlFor="email">
                                    Password
                                </label>
                                <input
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    id="password"
                                    type="password"
                                    placeholder="Password Usuario"
                                    value={formik.values.password}
                                    onChange={formik.handleChange}
                                    onBlur={formik.handleBlur}
                                />
                                {
                                    formik.touched.password && formik.errors.password ? (
                                        <div className="my-2 bg-red-100 border-red-500 text-red-700 p-2">
                                            <p>Error</p>
                                            <p>{formik.errors.password}</p>
                                        </div>
                                    ) : null
                                }
                            </div>
                        </form>

                        <input
                            type="submit"
                            className="bg-gray-800 w-full mt-5 p-2 text-white uppercase hover:cursor-pointer hover:bg-gray-900"
                            value="Iniciar Sesion"
                        />
                    </div>
                </div>
            </Layout>
        </>
    );
}

export default NuevaCuenta;
